Scripts.OnClick:
if ( self:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOn");
else
	PlaySound("igMainMenuOptionCheckBoxOff");
end
local tracked = AchievementButton_ToggleTracking(self:GetParent().id);
if ( not tracked ) then
	self:SetChecked(false);
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if ( self:GetChecked() ) then
	GameTooltip:SetText(UNTRACK_ACHIEVEMENT_TOOLTIP, nil, nil, nil, nil, true);
else
	GameTooltip:SetText(TRACK_ACHIEVEMENT_TOOLTIP, nil, nil, nil, nil, true);
end

GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if ( self.name ) then
	GameTooltip:AddDoubleLine(self.name, self.date, nil, nil, nil, .5, .5, .5);
end
if ( self.desc ) then
	GameTooltip:AddLine(self.desc, 1, 1, 1, true);
end
if ( self.numCriteria ) then
	if ( self.numCriteria > 30 ) then
		for i = 1, self.numCriteria, 2 do
			GameTooltip:AddDoubleLine(self["criteria"..i], self["criteria"..(i+1)]);
		end
	else
		for i = 1, self.numCriteria do
			GameTooltip:AddLine(self["criteria"..i]);
		end
	end
end
GameTooltip:Show();

local tooltip = self.tooltip;
if ( tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(tooltip, nil, nil, nil, nil, true);
end
