Scripts.OnMouseDown:
if( self:IsEnabled() == 1 ) then
	self.bevel:Hide();
	self.shadow:Hide();
end

Scripts.OnMouseUp:
if( self:IsEnabled() == 1 ) then
	self.bevel:Show();
	self.shadow:Show();
end

Scripts.OnEnter:
if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_LEFT");
	GameTooltip:SetItemByID(self.itemID);
	self.showingTooltip = true;
end

Scripts.OnClick:
if IsModifiedClick("CHATLINK") and ChatEdit_GetActiveWindow() then
	if self.link then
		ChatEdit_InsertLink(self.link);
	end
	return;
end
EncounterJournal_DisplayInstance(self.instanceID);
PlaySound("igSpellBookOpen");

Scripts.OnEnter:
if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_LEFT");
	GameTooltip:SetItemByID(self.itemID);
	self.showingTooltip = true;
end

if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetItemByID(self.itemID);
end
self.showingTooltip = true;


if self.name then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.name,1,1,1);
	if self.description then
		GameTooltip:AddLine(self.description, nil, nil, nil, 1);
	end
	GameTooltip:Show();
end

Scripts.OnClick:
if IsModifiedClick("CHATLINK") and ChatEdit_GetActiveWindow() then
	if self.link then
		ChatEdit_InsertLink(self.link);
	end
	return;
end
EncounterJournal_DisplayEncounter(self.encounterID);
PlaySound("igAbiliityPageTurn");

Scripts.OnEnter:
if self.tooltipTitle then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipTitle,1,1,1);
	GameTooltip:AddLine(self.tooltipText, nil, nil, nil, 1);
	GameTooltip:Show();
end

Scripts.OnClick:
if self.displayInfo then
	EncounterJournal_DisplayCreature(self);
end
self:Enable();
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnLoad:
self.textures = {
	expanded = { 
		up = {self.eLeftUp, self.eMidUp, self.eRightUp},
		down = {self.eLeftDown, self.eMidDown, self.eRightDown}
	},
	collapsed = { 
		up = {self.cLeftUp, self.cMidUp, self.cRightUp},
		down = {self.cLeftDown, self.cMidDown, self.cRightDown}
	},
}
self.tex = self.textures.collapsed;

Scripts.OnShow:
local oldtex = self.textures.expanded;
if self:GetParent().expanded then
	self.tex = self.textures.expanded;
	oldtex = self.textures.collapsed;
	self.expandedIcon:SetTextColor(0.929, 0.788, 0.620);
	self.title:SetTextColor(0.929, 0.788, 0.620);
else
	self.tex = self.textures.collapsed;
	self.expandedIcon:SetTextColor(0.827, 0.659, 0.463);
	self.title:SetTextColor(0.827, 0.659, 0.463);
end

oldtex.up[1]:Hide();
oldtex.up[2]:Hide();
oldtex.up[3]:Hide();
oldtex.down[1]:Hide();
oldtex.down[2]:Hide();
oldtex.down[3]:Hide();


self.tex.up[1]:Show();
self.tex.up[2]:Show();
self.tex.up[3]:Show();
self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

Scripts.OnClick:
if IsModifiedClick("CHATLINK") and ChatEdit_GetActiveWindow() then
	if self.link then
		ChatEdit_InsertLink(self.link);
	end
	return;
end

EncounterJournal_ToggleHeaders(self:GetParent())
self:GetScript("OnShow")(self);
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnMouseDown:
self.tex.up[1]:Hide();
self.tex.up[2]:Hide();
self.tex.up[3]:Hide();

self.tex.down[1]:Show();
self.tex.down[2]:Show();
self.tex.down[3]:Show();

Scripts.OnMouseUp:
self.tex.up[1]:Show();
self.tex.up[2]:Show();
self.tex.up[3]:Show();

self.tex.down[1]:Hide();
self.tex.down[2]:Hide();
self.tex.down[3]:Hide();

Scripts.OnEnter:
if ( self.title:IsTruncated() ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.title:GetText(), nil, nil, nil, nil, 1);
end

Scripts.OnClick:
if (not HandleModifiedItemClick(self.link)) then
	EncounterJournal_Loot_OnClick(self);
	PlaySound("igSpellBookOpen");
else
	PlaySound("igMainMenuOption");
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT");
GameTooltip:SetItemByID(self.itemID);
self.showingTooltip = true;
self:SetScript("OnUpdate", EncounterJournal_Loot_OnUpdate);

Scripts.OnLeave:
GameTooltip:Hide();
self.showingTooltip = false;
self:SetScript("OnUpdate", nil);
ResetCursor();

Scripts.OnClick:
self:Disable();
self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
self.selectedGlow:Show();
local dungeonsTab = self:GetParent().dungeonsTab;
dungeonsTab:Enable();
dungeonsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
dungeonsTab.selectedGlow:Hide();
EncounterJournal_ListInstances();
PlaySound("igMainMenuOptionCheckBoxOn");

self:Disable();
self:GetFontString():SetTextColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
self.selectedGlow:Show();
local raidsTab = self:GetParent().raidsTab;
raidsTab:Enable();
raidsTab:GetFontString():SetTextColor(HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
raidsTab.selectedGlow:Hide();
EncounterJournal_ListInstances();
PlaySound("igMainMenuOptionCheckBoxOn");

local _, _, _, _, _, dungeonAreaMapID = EJ_GetInstanceInfo();
if dungeonAreaMapID and dungeonAreaMapID > 0 then
	ShowUIPanel(WorldMapFrame);
	SetMapByID(dungeonAreaMapID);
end

Scripts.OnLoad:
local l, t, _, b, r = self.upLeft:GetTexCoord();
self.upLeft:SetTexCoord(l, l + (r-l)/2, t, b);
l, t, _, b, r = self.upRight:GetTexCoord();
self.upRight:SetTexCoord(l + (r-l)/2, r, t, b);


l, t, _, b, r = self.downLeft:GetTexCoord();
self.downLeft:SetTexCoord(l, l + (r-l)/2, t, b);
l, t, _, b, r = self.downRight:GetTexCoord();
self.downRight:SetTexCoord(l + (r-l)/2, r, t, b);


l, t, _, b, r = self.highLeft:GetTexCoord();
self.highLeft:SetTexCoord(l, l + (r-l)/2, t, b);
l, t, _, b, r = self.highRight:GetTexCoord();
self.highRight:SetTexCoord(l + (r-l)/2, r, t, b);

local classes = {EJ_GetAvailableClasses()};
local name, class;
for i=1,#classes,2 do
	name = classes[i];
	class = classes[i+1];

	local coords = CLASS_ICON_TCOORDS[class];
	local button = self["class"..floor((i+1)/2)];
	button.name = name;

	button:GetNormalTexture():SetTexCoord(coords[1], coords[2], coords[3], coords[4]);
	button:GetPushedTexture():SetTexCoord(coords[1], coords[2], coords[3], coords[4]);
end

Scripts.OnClick:
if not self:GetParent().classFilter:IsShown() then
	self:GetParent().classFilter:Show();
else
	self:GetParent().classFilter:Hide();
end
PlaySound("igMainMenuOption");

Scripts.OnLoad:
self:SetPoint("TOPLEFT", self:GetParent():GetParent(), "TOPRIGHT", -340, -13);

