Scripts.OnClick:
if ( IsModifiedClick() ) then
	GarrisonFollowerListButton_OnModifiedClick(self, button);
else
	GarrisonFollowerListButton_OnClick(self, button);
end

if ( IsModifiedClick("CHATLINK") ) then
	local abilityLink = C_Garrison.GetFollowerAbilityLink(self.abilityID);
	if (abilityLink) then
		ChatEdit_InsertLink(abilityLink);
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if (self.itemID) then
	GameTooltip:SetItemByID(self.itemID);
	return;
end
if (self.title) then
	GameTooltip:SetText(self.title);
end
if (self.tooltip) then
	GameTooltip:AddLine(self.tooltip, 1, 1, 1, true);
end
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if (self.itemID) then
	GameTooltip:SetItemByID(self.itemID);
	return;
end
if (self.title) then
	GameTooltip:SetText(self.title);
end
if (self.tooltip) then
	GameTooltip:AddLine(self.tooltip, 1, 1, 1, true);
end
GameTooltip:Show();


GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetText(C_Garrison.GetFollowerAbilityName(self.id), NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
GameTooltip:AddLine(C_Garrison.GetFollowerAbilityDescription(self.id), HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b, true);
GameTooltip:Show();

Scripts.OnLoad:
GarrisonCinematicModelBase_OnLoad(self);
self:SetDisplayInfo(54912);
self:SetTargetDistance(0);
self:SetHeightFactor(.25);
self:SetFacing(-.4);
self:InitializeCamera(.8);
