Scripts.OnClick:
if ( IsModifiedClick("CHATLINK") ) then
	local abilityLink = C_Garrison.GetFollowerAbilityLink(self.abilityID);
	if (abilityLink) then
		ChatEdit_InsertLink(abilityLink);
	end
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if (self.itemID) then
	GameTooltip:SetItemByID(self.itemID);
	return;
end
if (self.currencyID and self.currencyID ~= 0) then
	GameTooltip:SetCurrencyByID(self.currencyID);
	return;
end
if (self.title) then
	GameTooltip:SetText(self.title);
end
if (self.tooltip) then
	GameTooltip:AddLine(self.tooltip, 1, 1, 1, true);
end
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if (self.itemID) then
	GameTooltip:SetItemByID(self.itemID);
	return;
end
if (self.currencyID and self.currencyID ~= 0) then
	GameTooltip:SetCurrencyByID(self.currencyID);
	return;
end				
if (self.title) then
	GameTooltip:SetText(self.title);
end
if (self.tooltip) then
	GameTooltip:AddLine(self.tooltip, 1, 1, 1, true);
end
GameTooltip:Show();


GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetText(C_Garrison.GetFollowerAbilityName(self.id), NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);
GameTooltip:AddLine(C_Garrison.GetFollowerAbilityDescription(self.id), HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b, true);
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
local itemLevel = GarrisonMissionFrame.MissionTab.MissionPage.missionInfo.iLevel;
GameTooltip:SetText(string.format(GARRISON_MISSION_EXPECTED_ILVL, itemLevel), 1, 1, 1);
GameTooltip:AddLine(GARRISON_MISSION_EXPECTED_ILVL_TOOLTIP, nil, nil, nil, 1);
GameTooltip:Show();

if (self:GetParent().MissionXP.hasBonusBaseXP) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(GARRISON_MISSION_BONUS_BASE_XP_TOOLTIP, nil, nil, nil, nil, true);
end

GameTooltip:ClearAllPoints();
GameTooltip:SetPoint("BOTTOMLEFT", self, "BOTTOMRIGHT", 10, 0);
GameTooltip:SetOwner(self, "ANCHOR_PRESERVE");
GameTooltip:SetText(GARRISON_MISSION_CHANCE_TOOLTIP_HEADER);
local missionID = GarrisonMissionFrame.MissionTab.MissionPage.missionInfo.missionID;
GameTooltip:AddLine(string.format(GARRISON_MISSION_PERCENT_CHANCE, C_Garrison.GetRewardChance(missionID)), HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b);
GameTooltip:AddLine(" ");
GameTooltip:AddLine(GARRISON_MISSION_CHANCE_TOOLTIP_TEXT, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1, 1);
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
local itemLevel = GarrisonMissionFrame.MissionComplete.currentMission.iLevel;
GameTooltip:SetText(string.format(GARRISON_MISSION_EXPECTED_ILVL, itemLevel), 1, 1, 1);
GameTooltip:AddLine(GARRISON_MISSION_EXPECTED_ILVL_TOOLTIP, nil, nil, nil, 1);
GameTooltip:Show();

Scripts.OnFinished:
if ( C_Garrison.CanOpenMissionChest(GarrisonMissionFrame.MissionComplete.currentMission.missionID) ) then
	local chest = self:GetParent();
	chest.Lock:Hide();
else
	self:GetParent().Lock:Show();
	self:GetParent().Lock:SetAlpha(1);
	GarrisonMissionFrame.MissionComplete.NextMissionButton:SetEnabled(true);
end

Scripts.OnLoad:
GarrisonCinematicModelBase_OnLoad(self);
self:SetDisplayInfo(54912);
self:SetTargetDistance(0);
self:SetHeightFactor(.25);
self:SetFacing(-.4);
self:InitializeCamera(.8);
