Scripts.OnEnter:
if (self.name) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.name, HIGHLIGHT_FONT_COLOR:GetRGB());
	if (self.longDescription) then
		GameTooltip:AddLine(self.longDescription);
	end
	GameTooltip:Show();
end

if self.itemID then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
	GameTooltip:SetItemByID(self.itemID);
end

Scripts.OnClick:
local parent = self:GetParent();
local parentFunction = parent:GetScript("OnClick");
if ( parentFunction ) then
	parentFunction(parent, button, down);
end

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_TOP");
	GameTooltip:SetText(self.tooltip, nil, nil, nil, nil, true);
end

Scripts.OnLeave:
if ( GameTooltip:GetOwner() == self ) then
	GameTooltip:Hide();
end

Scripts.OnEnter:
if (self.rewardInfo) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	if (self.rewardInfo:SetTooltip()) then
		GameTooltip:Show();
	end
end

Scripts.OnClick:
if (self:GetParent().BrawlHelpBox:IsShown()) then
	self:GetParent().BrawlHelpBox:Hide();
	SetCVarBitfield("closedInfoFrames",	LE_FRAME_TUTORIAL_BRAWL, true);
end

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1, true);
	GameTooltip:Show();
end

if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1, true);
end
