Scripts.OnEnter:
if (self.role) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(HIGHLIGHT_FONT_COLOR_CODE.._G[self.role]..FONT_COLOR_CODE_CLOSE);
	if (_G["TALENT_SPEC_"..self.role.."_TOOLTIP"]) then
		GameTooltip:AddLine(_G["TALENT_SPEC_"..self.role.."_TOOLTIP"], NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b, 1);
	end
	GameTooltip:Show();
end

if (self.spellId) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetSpellByID(self.spellId);
	if (self.spellId2) then
		local name1 = GetSpellInfo(self.spellId);
		local name2 = GetSpellInfo(self.spellId2);
		GameTooltip:SetText(HIGHLIGHT_FONT_COLOR_CODE..name1.."/"..name2..FONT_COLOR_CODE_CLOSE);
		GameTooltip:AddSpellByID(self.spellId);
		GameTooltip:AddLine(" ");
		GameTooltip:AddSpellByID(self.spellId2);
	else
		GameTooltip:SetSpellByID(self.spellId);
	end
	if (self.extraTooltip) then
		GameTooltip:AddLine(" ");
		GameTooltip:AddLine(self.extraTooltip, nil, nil, nil, true);
	end
	GameTooltip:Show();
end

if (self.spellId) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetSpellByID(self.spellId);
	GameTooltip:Show();
end

if (self.LockIcon:IsShown()) then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT", 10, -10);
	GameTooltip:SetText(TALENT_TREE_LOCKED);
	GameTooltip:AddLine(TALENT_TREE_LOCKED_TOOLTIP, HIGHLIGHT_FONT_COLOR.r, HIGHLIGHT_FONT_COLOR.g, HIGHLIGHT_FONT_COLOR.b, 1);
	GameTooltip:Show();
end

Scripts.OnClick:
PlayerTalentFrameTalents.summariesShownWhenPrimary = false;
if ( GetCVarBool("previewTalentsOption") ) then
	SetPreviewPrimaryTalentTree(self:GetParent().talentTree, self:GetParent().talentGroup);
else
	SetPrimaryTalentTree(self:GetParent().talentTree);
end

if (PlayerTalentFrame.primaryTree or GetNumTalentPoints() == 0) then
	PlayerTalentFrameTalents.summariesShownWhenPrimary = (not PlayerTalentFrameTalents.summariesShownWhenPrimary);
else
	PlayerTalentFrameTalents.summariesShownWhenNoPrimary = (not PlayerTalentFrameTalents.summariesShownWhenNoPrimary);
end
PlayerTalentFrame_UpdateControls();

Scripts.OnEnter:
if ( self.tooltip ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip);
	if ( self.tooltipDamage ) then
		GameTooltip:AddLine(self.tooltipDamage, "", 1, 1, 1);
	end
	GameTooltip:Show();
end

Scripts.OnEvent:
local arg1 = ...;
if ((event == "UNIT_PET" and arg1 == "player") or arg1 == "pet") then
	PlayerTalentFrame_UpdatePetInfo(PlayerTalentFrame);
end

Scripts.OnLoad:
self.talentTree = 1;
self.pet = true;
self.position = nil;
PlayerTalentFramePanel_OnLoad(self);
self.talentButtonSize = 32;
self.buttonSpacingX = 52;
self.buttonSpacingY = 50;
self.initialOffsetX = 24;
self.initialOffsetY = 72;
