Scripts.OnClick:
if ( this.sortType ) then
	SortWho(this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

if ( this.sortType ) then
	SortGuildRoster(this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
GuildControlSetRankFlag(this:GetID(), this:GetChecked());
GuildControlPopupAcceptButton:Enable();

PanelTemplates_Tab_OnClick(FriendsFrame);
FriendsFrame_OnShow();
GuildControlPopupFrame:Hide();
GuildMemberDetailFrame:Hide();
GuildInfoFrame:Hide();
GuildFrame:Hide();

if ( UnitCanCooperate("player", "target") ) then
	AddFriend(UnitName("target"));
else
	StaticPopup_Show("ADD_FRIEND");
end

if ( UnitCanCooperate("player", "target") ) then
	AddIgnore(UnitName("target"));
else
	StaticPopup_Show("ADD_IGNORE");
end

SetGuildRosterSelection(0);
if ( this:GetChecked() ) then
	SHOW_OFFLINE_GUILD_MEMBERS = 1;
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	SHOW_OFFLINE_GUILD_MEMBERS = 0;
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SetGuildRosterShowOffline(SHOW_OFFLINE_GUILD_MEMBERS);
GuildStatus_Update();

if ( GuildControlPopupFrame:IsVisible() ) then
	GuildControlPopupFrame:Hide();
else
	GuildControlPopupFrame:Show();
end

Scripts.OnMouseDown:
if ( CanEditGuildInfo() ) then
	GuildInfoEditBox:SetFocus();
else
	GuildInfoEditBox:ClearFocus();
end

Scripts.OnMouseUp:
if ( CanEditGuildInfo() ) then
	GuildInfoEditBox:SetFocus();
else
	GuildInfoEditBox:ClearFocus();
end

Scripts.OnTextChanged:
local scrollBar = getglobal(this:GetParent():GetName().."ScrollBar")
this:GetParent():UpdateScrollChildRect();
local min;
local max;
min, max = scrollBar:GetMinMaxValues();
if ( max > 0 and (this.max ~= max) ) then
	this.max = max;
	scrollBar:SetValue(max);
end

Scripts.OnShow:
local guildInfoText = GetGuildInfoText();
if ( not guildInfoText ) then
	guildInfoText = "";
end
if ( CanEditGuildInfo() ) then
	if ( guildInfoText ~= "" ) then
		GuildInfoEditBox:SetText(guildInfoText);
	else
		GuildInfoEditBox:SetText(GUILD_INFO_EDITLABEL);
	end
	GuildInfoEditBox:SetTextColor(1, 1, 1);
	GuildInfoEditBox:EnableMouse(1);
	GuildInfoSaveButton:Enable();
else
	GuildInfoEditBox:SetText(guildInfoText);
	GuildInfoEditBox:SetTextColor(0.65, 0.65, 0.65);
	GuildInfoEditBox:EnableMouse();
	GuildInfoSaveButton:Disable();
end
