Scripts.OnClick:
if ( this.sortType ) then
	SortWho(this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

if ( this.sortType ) then
	SortGuildRoster(this.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
GuildControlSetRankFlag(this:GetID(), this:GetChecked());
GuildControlPopupAcceptButton:Enable();

if ( UnitCanCooperate("player", "target") ) then
	AddFriend(UnitName("target"));
else
	StaticPopup_Show("ADD_FRIEND");
end

if ( UnitCanCooperate("player", "target") ) then
	AddIgnore(UnitName("target"));
else
	StaticPopup_Show("ADD_IGNORE");
end

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SetLookingForGroup(this:GetChecked());

SetGuildRosterSelection(0);
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
SetGuildRosterShowOffline(this:GetChecked());
if ( FriendsFrame.playerStatusFrame ) then
	GuildPlayerStatus_Update();
else
	GuildStatus_Update();
end

if ( GuildControlPopupFrame:IsVisible() ) then
	GuildControlPopupFrame:Hide();
else
	GuildControlPopupFrame:Show();
end

Scripts.OnEnter:
if ( SHOW_NEWBIE_TIPS == "1" ) then
	GameTooltip_AddNewbieTip(GUILD_MOTD_LABEL2, 1.0, 1.0, 1.0, GUILDNOTE_BUTTON_TOOLTIP, 1);
else
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(GUILDMOTD_BUTTON_TOOLTIP);
end

Scripts.OnUpdate:
if ( GetGuildRosterSelection() == 0 ) then
	this:Disable();
else
	this:Enable();
end

Scripts.OnEnter:
if ( SHOW_NEWBIE_TIPS == "1" ) then
	GameTooltip_AddNewbieTip(PUBLIC_NOTE, 1.0, 1.0, 1.0, NEWBIE_TOOLTIP_GUILDPUBLICNOTE, 1);
else
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(GUILDNOTE_BUTTON_TOOLTIP);
end

Scripts.OnUpdate:
if ( (GetGuildRosterSelection() == 0) or (not CanViewOfficerNote()) ) then
	this:Disable();
else
	this:Enable();
end

Scripts.OnEnter:
if ( SHOW_NEWBIE_TIPS == "1" ) then
	GameTooltip_AddNewbieTip(GUILD_OFFICER_NOTE, 1.0, 1.0, 1.0, GUILDOFFICERNOTE_BUTTON_TOOLTIP, 1);
else
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(GUILDOFFICERNOTE_BUTTON_TOOLTIP);
end

Scripts.OnClick:
PanelTemplates_Tab_OnClick(FriendsFrame);
--[[ GuildRoster();		I don't think this one's necessary... ]]
FriendsFrame_OnShow();
