Scripts.OnShow:
local height = self.Text:GetHeight()+30;
if ( self.OkayButton:IsShown() ) then
	height = height + 60;
end
self:SetHeight(height);

Scripts.OnMouseDown:
if( self:IsEnabled() ) then
	self.bevel:SetPoint("CENTER", self, "CENTER", 2, -2);
	self.Shadow:SetSize(70, 70);
end

Scripts.OnMouseUp:
if( self:IsEnabled() ) then
	self.bevel:SetPoint("CENTER", self, "CENTER", 0, 0);
	self.Shadow:SetSize(76, 76);
end

Scripts.OnMouseDown:
if( self:IsEnabled() ) then
	self.BevelEdge:SetPoint("CENTER", self, "CENTER", 2, -2);
	self.Shadow:SetSize(63, 63);
end

Scripts.OnMouseUp:
if( self:IsEnabled() ) then
	self.BevelEdge:SetPoint("CENTER", self, "CENTER", 0, 0);
	self.Shadow:SetSize(68, 68);
end

Scripts.OnLoad:
local scrollBar = _G[self:GetName().."ScrollBar"];
scrollBar:ClearAllPoints();
scrollBar:SetPoint("TOPLEFT", self, "TOPRIGHT", 11, 10);
scrollBar:SetPoint("BOTTOMLEFT", self, "BOTTOMRIGHT", 11, 12);
GlueScrollFrame_OnScrollRangeChanged(self, yrange);

Scripts.OnMouseDown:
if( self:IsEnabled() ) then
	self.bevel:SetPoint("CENTER", self, "CENTER", 2, -2);
	self.shadow:SetSize(52, 52);
end

Scripts.OnMouseUp:
if( self:IsEnabled() ) then
	self.bevel:SetPoint("CENTER", self, "CENTER", 0, 0);
	self.shadow:SetSize(58, 58);
end

Scripts.OnClick:
PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
if ( self.infoShown ) then
	self.infoShown = nil;
	CharCreateMoreInfoButtonTopGlow:Hide();
	CharCreateMoreInfoButtonBottomGlow:Hide();
	CharCreateRaceInfoFrame:Hide();
	CharCreateClassInfoFrame:Hide();
else
	self.infoShown = true;
	CharCreateMoreInfoButtonTopGlow:Show();
	CharCreateMoreInfoButtonBottomGlow:Show();
	CharCreateRaceInfoFrame:Show();
	CharCreateClassInfoFrame:Show();
end

CharacterCreateAlternateFormTop:SetChecked(false);
CharacterCreateAlternateFormBottom:SetChecked(true);
if ( not C_CharacterCreation.IsViewingAlteredForm() ) then
	PlaySound(SOUNDKIT.GS_CHARACTER_CREATION_CLASS);
	C_CharacterCreation.SetViewingAlteredForm(true);
	CharCreate_PrepPreviewModels(true);
	CharCreate_ResetFeaturesDisplay();
end

PlaySound(SOUNDKIT.GS_CHARACTER_CREATION_CLASS);
if ( C_CharacterCreation.GetSelectedSex() ~= Enum.Unitsex.Male ) then
	SetCharacterGender(Enum.Unitsex.Male);
else
	self:SetChecked(true);
end

PlaySound(SOUNDKIT.GS_CHARACTER_CREATION_CLASS);
if ( C_CharacterCreation.GetSelectedSex() ~= Enum.Unitsex.Female ) then
	SetCharacterGender(Enum.Unitsex.Female);
else
	self:SetChecked(true);
end
