Scripts.OnMouseDown:
if ( self:IsEnabled() ) then
	self.normalTexture:SetPoint("CENTER", 1, -1);
	self.highlightTexture:SetPoint("CENTER", 1, -1);
end

Scripts.OnEnter:
if ( self.selection:IsShown() ) then
	CharacterSelectButton_ShowMoveButtons(self);
end
if ( self.isVeteranLocked and CharSelectAccountUpgradeButton:IsEnabled()) then
	GlueTooltip:SetText(CHARSELECT_CHAR_LIMITED_TOOLTIP, nil, nil, nil, nil, true);
	GlueTooltip:Show();
	GlueTooltip:SetOwner(self, "ANCHOR_LEFT", -16, -5);
	CharSelectAccountUpgradeButtonPointerFrame:Show();
	CharSelectAccountUpgradeButtonGlow:Show();
end

Scripts.OnLeave:
if ( self.upButton:IsShown() and not (self.upButton:IsMouseOver() or self.downButton:IsMouseOver()) ) then
	self.upButton:Hide();
	self.downButton:Hide();
end
CharSelectAccountUpgradeButtonPointerFrame:Hide();
CharSelectAccountUpgradeButtonGlow:Hide();
GlueTooltip:Hide();

Scripts.OnEnter:
GlueTooltip:SetOwner(self, "ANCHOR_LEFT", 4, -8);
if ( self:IsEnabled() ) then
	GlueTooltip:SetText(self.tooltip, 1.0, 1.0, 1.0);
else
	GlueTooltip:SetText(self.disabledTooltip, 1.0, 1.0, 1.0);
end

Scripts.OnLoad:
local parent = self:GetParent();
if ( not parent.featureFrames ) then
	parent.featureFrames = {};
end
tinsert(parent.featureFrames, self);

Scripts.OnClick:
-- This should be disabled, but due to timing of events it may not be checked yet
if not CharacterSelect_AllowedToEnterWorld() then
	CharacterSelect_UpdateButtonState();
	return;
end
CharacterSelect_EnterWorld();

Scripts.OnLeave:
if GlueTooltip:GetOwner() == self then
	GlueTooltip:Hide();
end

Scripts.OnLoad:
local fontString = self:GetFontString();
fontString:SetPoint("CENTER", 8, 2);
self.Logo:ClearAllPoints();
self.Logo:SetPoint("RIGHT", fontString, "LEFT", -2, 0);

Scripts.OnMouseDown:
local fontString = self:GetFontString();
self.Logo:SetPoint("RIGHT", fontString, "LEFT", -1, -2);
self.down = true;

Scripts.OnMouseUp:
local fontString = self:GetFontString();
self.Logo:SetPoint("RIGHT", fontString, "LEFT", -2, 0);
self.down = false;

Scripts.OnEnter:
local buttonTooltip = CHARACTER_UPGRADE_POPUP_TRY_NEW_CLASS_DISABLED_TOOLTIP;
if C_CharacterServices.IsTrialBoostEnabled() then
	local boostLevel = C_CharacterCreation.GetTrialBoostStartingLevel();
	buttonTooltip = CHARACTER_UPGRADE_POPUP_TRY_NEW_CLASS_TOOLTIP:format(boostLevel);
end
GlueTooltip:SetOwner(self, "ANCHOR_RIGHT");
GlueTooltip:SetText(buttonTooltip, nil, nil, nil, nil, true);

GlueTooltip:SetOwner(self);
if ( self:IsEnabled() ) then
	GlueTooltip:SetText(PAID_FACTION_CHANGE_TOOLTIP, 1.0, 1.0, 1.0);
else
	GlueTooltip:SetText(nil, 1.0, 1.0, 1.0);
end

Scripts.OnLoad:
ServerAlert_OnLoad(self);
self:RegisterEvent("LAUNCHER_LOGIN_STATUS_CHANGED");
if ( not C_Login.IsLauncherLogin() ) then
	ServerAlert_Disable(self);
end

Scripts.OnEvent:
if ( event == "LAUNCHER_LOGIN_STATUS_CHANGED" ) then
	if ( C_Login.IsLauncherLogin() ) then
		ServerAlert_Enable(self);
	else
		ServerAlert_Disable(self);
	end
else
	ServerAlert_OnEvent(self, event, ...);
end

Scripts.OnTextChanged:
if ( strupper(self:GetText()) == strupper(DELETE_CONFIRM_STRING) ) then
	CharacterDeleteButton1:Enable();
else
	CharacterDeleteButton1:Disable();
end
