Scripts.OnMouseDown:
if (self:IsEnabled()) then
	local fontString = self:GetFontString();
	self.Logo:SetPoint("RIGHT", fontString, "LEFT", -1, -2);
end

Scripts.OnMouseUp:
if (self:IsEnabled()) then
	local fontString = self:GetFontString();
	self.Logo:SetPoint("RIGHT", fontString, "LEFT", -2, 0);
end

Scripts.OnEnter:
if (self.tooltip) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltip, RED_FONT_COLOR.r, RED_FONT_COLOR.g, RED_FONT_COLOR.b, true);
	GameTooltip:Show();
end

Scripts.OnShow:
local balanceEnabled = select(3, C_WowTokenPublic.GetCommerceSystemStatus());
self.LeftDisplay.Tutorial3:SetIndentedWordWrap(true);
if (balanceEnabled) then
	self.LeftDisplay.Tutorial3:SetText(TUTORIAL_TOKEN_GAME_TIME_STEP_2_BALANCE:format(WowTokenRedemptionFrame_GetBalanceString()));
else
	self.LeftDisplay.Tutorial3:SetText(TUTORIAL_TOKEN_GAME_TIME_STEP_2);
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetText(TOKEN_BUYOUT_PRICE_UPDATES, nil, nil, nil, nil, true);
GameTooltip:Show();

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	BrowseBidPriceCopper:SetFocus();
else
	BrowseMinLevel:SetFocus();
end

if ( IsShiftKeyDown() ) then
	BrowseName:SetFocus();
else
	BrowseMaxLevel:SetFocus();
end

if ( IsShiftKeyDown() ) then
	BrowseMinLevel:SetFocus();
else
	BrowseBidPriceGold:SetFocus();
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
if ( GetAuctionSellItemInfo() ) then
	local hasCooldown, speciesID, level, breedQuality, maxHealth, power, speed, name = GameTooltip:SetAuctionSellItem();
	if(speciesID and speciesID > 0) then
		BattlePetToolTip_Show(speciesID, level, breedQuality, maxHealth, power, speed, name);
	end
else
	GameTooltip:SetText(AUCTION_ITEM_TEXT, 1.0, 1.0, 1.0);
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	if ( self.prevFocus ) then
		self.prevFocus:SetFocus();
	else
		self:ClearFocus();
	end
else
	if ( self.nextFocus ) then
		self.nextFocus:SetFocus();
	else
		self:ClearFocus();
	end
end

Scripts.OnEnterPressed:
if ( self.nextFocus ) then
	self.nextFocus:SetFocus();
else
	self:ClearFocus();
end

Scripts.OnTabPressed:
if ( IsShiftKeyDown() ) then
	if ( self.prevFocus ) then
		self.prevFocus:SetFocus();
	else
		self:ClearFocus();
	end
else
	if ( self.nextFocus ) then
		self.nextFocus:SetFocus();
	else
		self:ClearFocus();
	end
end

Scripts.OnEnterPressed:
if ( self.nextFocus ) then
	self.nextFocus:SetFocus();
else
	self:ClearFocus();
end

Scripts.OnClick:
local stackSize = AuctionsStackSizeEntry:GetNumber();
if ( stackSize == 0 ) then
	AuctionsStackSizeEntry:SetText(1);
	stackSize = 1;
end
if ( AuctionsItemButton.stackCount and AuctionsItemButton.totalCount and AuctionsItemButton.totalCount > 0) then
	AuctionsNumStacksEntry:SetNumber( floor(AuctionsItemButton.totalCount / stackSize) );
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT", -100, -50);
GameTooltip:SetText(TOKEN_CREATE_AUCTION_LOCKED_PRICE, nil, nil, nil, nil, true);
GameTooltip:Show();

Scripts.OnClick:
if ( GetSelectedAuctionItem("owner") and (GetSelectedAuctionItem("owner") > 0) ) then
	StaticPopup_Show("CANCEL_AUCTION");
end

Scripts.OnHide:
PlaySound(SOUNDKIT.AUCTION_WINDOW_CLOSE);
StaticPopup_Hide("BUYOUT_AUCTION");
StaticPopup_Hide("CANCEL_AUCTION");
WowTokenGameTimeTutorial:Hide();
CloseSideDressUpFrame(self);
SetAuctionsTabShowing(false);
CloseAuctionHouse();

Scripts.OnLoad:
AuctionProgressBar.Spark:Hide();
AuctionProgressBar.Flash:Hide();
AuctionProgressBar.Icon:Show();
AuctionProgressBar.Icon:SetWidth(24);
AuctionProgressBar.Icon:SetHeight(24);
AuctionProgressBar.Icon:SetPoint("RIGHT", "$parent", "LEFT", -10, 2);
