Scripts.OnEditFocusGained:
if ( self:GetText() == CALENDAR_EVENT_DESCRIPTION ) then
	self:HighlightText();
end

Scripts.OnEditFocusLost:
self:HighlightText(0, 0);
if ( self:GetText() == "" ) then
	self:SetText(CALENDAR_EVENT_DESCRIPTION);
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT");
GameTooltip:SetText(CALENDAR_TOOLTIP_AUTOAPPROVE, nil, nil, nil, nil, true);
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_LEFT");
GameTooltip:SetText(CALENDAR_TOOLTIP_LOCKEVENT, nil, nil, nil, nil, true);
GameTooltip:Show();

Scripts.OnTabPressed:
if ( not AutoCompleteEditBox_OnTabPressed(self) ) then
	EditBox_HandleTabbing(self, CALENDAR_CREATEEVENTFRAME_TAB_LIST);
end

Scripts.OnEscapePressed:
if ( not AutoCompleteEditBox_OnEscapePressed(self) ) then
	EditBox_ClearFocus(self);
end

Scripts.OnTextChanged:
if ( userInput ) then
	local isGuildEvent = C_Calendar.EventGetCalendarType() == "GUILD_EVENT";
	if ( isGuildEvent ) then
		AutoCompleteEditBox_SetAutoCompleteSource(self, GetAutoCompleteResults, AUTOCOMPLETE_LIST.CALENDARGUILDEVENT.include, AUTOCOMPLETE_LIST.CALENDARGUILDEVENT.exclude);
	else
		AutoCompleteEditBox_SetAutoCompleteSource(self, GetAutoCompleteResults, AUTOCOMPLETE_LIST.CALENDAREVENT.include, AUTOCOMPLETE_LIST.CALENDAREVENT.exclude);
	end
	AutoCompleteEditBox_OnTextChanged(self, userInput);
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT");
GameTooltip:SetText(CALENDAR_TOOLTIP_MASSINVITE, nil, nil, nil, nil, true);
GameTooltip:Show();

Scripts.OnEditFocusGained:
if ( not CalendarMassInviteFrame.selectedClubId ) then
	self:ClearFocus();
end

if ( not CalendarMassInviteFrame.selectedClubId ) then
	self:ClearFocus();
end
