Scripts.OnClick:
PlaySound(SOUNDKIT.IG_MAINMENU_CLOSE);
if (self:GetParent().clubInfo.isGuild) then
	self:GetParent().WarningDialog:Show();
else
	self:GetParent():AcceptInvitation();
end

self:OnClick();
if (not self:GetChecked()) then
	self:GetParent().EditBox:SetText("");
	self:GetParent().EditBox.Text:Show();
end

Scripts.OnShow:
local disabledReason = C_ClubFinder.GetClubFinderDisableReason();
local disabled = disabledReason ~= nil;
self.GuildDescription:SetAlpha(disabled and 0 or 1);
self.ErrorDescription:SetShown(disabled);
if disabledReason == Enum.ClubFinderDisableReason.Muted then
	self.ErrorDescription:SetText(RED_FONT_COLOR:WrapTextInColorCode(COMMUNITY_FEATURE_UNAVAILABLE_MUTED));
elseif disabledReason == Enum.ClubFinderDisableReason.Silenced then
	self.ErrorDescription:SetText(RED_FONT_COLOR:WrapTextInColorCode(COMMUNITY_FEATURE_UNAVAILABLE_SILENCED));
end
