Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
local itemLevel = self:GetParent().missionInfo.iLevel;
GameTooltip:SetText(string.format(GARRISON_MISSION_EXPECTED_ILVL, itemLevel), 1, 1, 1);
GameTooltip:AddLine(GARRISON_MISSION_EXPECTED_ILVL_TOOLTIP, nil, nil, nil, 1);
GameTooltip:Show();

Scripts.OnKeyDown:
if ( key == "SPACE" ) then
	self:SetPropagateKeyboardInput(false);
	self.ViewButton:Click();
else
	self:SetPropagateKeyboardInput(true);
end

Scripts.OnFinished:
local missionCompleteFrame = self:GetParent():GetParent():GetParent();
if ( C_Garrison.CanOpenMissionChest(missionCompleteFrame.currentMission.missionID) ) then
	local chest = self:GetParent();
	chest.Lock:Hide();
else
	self:GetParent().Lock:Show();
	self:GetParent().Lock:SetAlpha(1);
	missionCompleteFrame.NextMissionButton:SetEnabled(true);
end
