PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
BNSendVerifiedBattleTagInvite();	-- unit should have been set with BNCheckBattleTagInviteToUnit or BNCheckBattleTagInviteToGuildMember
