Scripts.OnEnter:
if ( self.tooltip ) then
	VideoOptionsTooltip:SetOwner(self, "ANCHOR_LEFT");
	VideoOptionsTooltip:SetText(self.tooltip, 1.0, 1.0, 1.0, 1, 1);
end

-- also called by $childButton
if(self.tooltip ~= nil) then
	VideoOptionsTooltip:SetOwner(self);
	if(self.name == nil) then
		self.name = " ";
	end
	VideoOptionsTooltip:SetText(self.name .. ":", nil, nil, nil, nil, 1);
	VideoOptionsTooltip:AddLine(self.tooltip, 1.0, 1.0, 1.0, 1.0, 1);
	VideoOptionsTooltip:Show();
end

if ( self.tooltip ) then
	VideoOptionsTooltip:SetOwner(self, "ANCHOR_LEFT");
	VideoOptionsTooltip:SetText(self.tooltip, 1.0, 1.0, 1.0, 1, 1);
end

-- also called by $childButton
if(self.tooltip ~= nil) then
	VideoOptionsTooltip:SetOwner(self);
	if(self.name == nil) then
		self.name = " ";
	end
	VideoOptionsTooltip:SetText(self.name .. ":", nil, nil, nil, nil, 1);
	VideoOptionsTooltip:AddLine(self.tooltip, 1.0, 1.0, 1.0, 1.0, 1);
	VideoOptionsTooltip:Show();
end

if(self.tooltip ~= nil) then
	VideoOptionsTooltip:SetOwner(self);
	if(self.name == nil) then
		self.name = " ";
	end
	VideoOptionsTooltip:SetText(self.name .. ":", nil, nil, nil, nil, 1);
	VideoOptionsTooltip:AddLine(self.tooltip, 1.0, 1.0, 1.0, 1.0, 1);
	VideoOptionsTooltip:Show();
end

Scripts.OnLoad:
self:SetMinMaxValues(GetMinRenderScale(), GetMaxRenderScale());
self:RegisterEvent("DISPLAY_SIZE_CHANGED");

local name = self:GetName();
self.type = CONTROLTYPE_SLIDER;
self.cvar = "RenderScale";
self.tooltip = OPTION_TOOLTIP_RENDER_SCALE;
self.name = RENDER_SCALE;

self.SetDisplayValue = self.SetValue;
self.SetValue = function(self, value)
self:SetDisplayValue(value);

if self:IsVisible() then
BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
end
end

self.GetCurrentValue = function(self)
return BlizzardOptionsPanel_GetCVarSafe(self.cvar);
end;

self.Text = _G[name.."Text"];
self.Text:SetFontObject("OptionsFontSmall");
self.Text:ClearAllPoints();
self.Text:SetPoint("RIGHT", self, "LEFT", -20, 0);
self.Text:SetText(RENDER_SCALE);

self.Value = _G[name.."Low"];
self.Value:ClearAllPoints();
self.Value:SetPoint("LEFT", self, "RIGHT", 3, 2);

self.Resolution = _G[name.."High"];
self.Resolution:ClearAllPoints();
self.Resolution:SetPoint("TOP", self, "BOTTOM", 0, 0);

VideoOptionsSlider_OnLoad(self);

Scripts.OnValueChanged:
if(userInput) then
	Graphics_EnableApply(self);
	self.newValue = value;
	BlizzardOptionsPanel_SetCVarSafe(self.cvar, value)
	VideoOptions_OnClick(self, value);
end
self.Value:SetFormattedText(PERCENTAGE_STRING, math.floor((value * 100) + .5));
local x, y = GetCurrentScaledResolution();
self.Resolution:SetText(x .."x".. y);

CloseDropDownMenus()
if(self.onvaluechanged) then
	BlizzardOptionsPanel_Slider_OnValueChanged(self, value);
end
local quality = floor(value + 0.5);
self.displayValue:SetText(quality);
if ( value == ClassicGraphicsQuality ) then
	Graphics_Quality_Title:SetText(GRAPHICS_QUALITY_CLASSIC);
elseif ( quality == GetDefaultGraphicsQuality() ) then
	Graphics_Quality_Title:SetText(GRAPHICS_QUALITY_RECOMENDED);
else
	Graphics_Quality_Title:SetText(GRAPHICS_QUALITY);
end

CloseDropDownMenus()
if(self.onvaluechanged) then
	BlizzardOptionsPanel_Slider_OnValueChanged(self, value);
end
local quality = floor(value + 0.5);
self.displayValue:SetText(quality);
if ( value == ClassicGraphicsQuality ) then
	RaidGraphics_Quality_Title:SetText(GRAPHICS_QUALITY_CLASSIC);
elseif ( quality == GetDefaultGraphicsQuality() ) then
	RaidGraphics_Quality_Title:SetText(GRAPHICS_QUALITY_RECOMENDED);
else
	RaidGraphics_Quality_Title:SetText(GRAPHICS_QUALITY);
end

Scripts.OnShow:
VideoOptionsFrameClassic:Show();
Display_EnableRaidSettingsText:SetPoint("LEFT", "RaidButton", "RIGHT", 30, 0);
if( not InGlue() ) then
	local inInstance, instanceType = IsInInstance();
	if ( inInstance and (instanceType == "raid" or instanceType == "pvp") ) then
		GraphicsOptions_SelectRaid();
		return;
	end
end
GraphicsOptions_SelectBase();

Scripts.OnLoad:
if(InGlue()) then
	self:Hide();
else
	local width = 2 * (Display_VerticalSyncDropDown:GetWidth() - 25 - 25);
	self:SetWidth(width );
	self.name = UI_SCALE;
	self.tooltip = OPTION_TOOLTIP_UI_SCALE;
	self.type = CONTROLTYPE_SLIDER;
	self.cvar = "uiscale";
	self.SetDisplayValue = self.SetValue;
	self.SetValue = function (self, value)
		self:SetDisplayValue(value);
		BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
	end
	local value = BlizzardOptionsPanel_GetCVarSafe("useUiScale")
	if(value ~= 0) then
		VideoOptions_Enable(self);
	else
		VideoOptions_Disable(self);
	end
	local name = self:GetName();
	_G[name.."Text"]:SetFontObject("OptionsFontSmall");
	_G[name.."Text"]:SetText(UI_SCALE);


	BlizzardOptionsPanel_RegisterControl(self, self:GetParent());
end

Scripts.OnValueChanged:
if(userInput) then
	Graphics_EnableApply(self);
	self.newValue = value;
end

Scripts.OnLoad:
if(InGlue()) then
	self:Hide();
else
	self.cvar = "useUiScale";
	self.SetValue = function (self, value)
		BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
	end
	VideoOptionsCheckbox_OnLoad(self);
end

Scripts.OnClick:
local parent = (self:GetParent()):GetName();
local slider = _G[parent.."UIScaleSlider"];
if ( self:GetChecked() ) then
	VideoOptions_Enable(slider);
else
	VideoOptions_Disable(slider);
end
BlizzardOptionsPanel_CheckButton_OnClick(self);
Graphics_EnableApply(self);

