Scripts.OnLoad:
BackdropTemplateMixin.OnBackdropLoaded(self);
if(InGlue()) then
	self:Hide();
else
	self.name = UI_SCALE;
	self.tooltip = OPTION_TOOLTIP_UI_SCALE;
	self.type = CONTROLTYPE_SLIDER;
	self.cvar = "uiscale";
	self.SetDisplayValue = self.SetValue;
	self.SetValue = function (self, value)
		self:SetDisplayValue(value);
		BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
	end
	local value = BlizzardOptionsPanel_GetCVarSafe("useUiScale")
	if(value ~= 0) then
		VideoOptions_Enable(self);
	else
		VideoOptions_Disable(self);
	end
	local name = self:GetName();
	local textString = _G[name.."Text"];
	textString:SetFontObject("OptionsFontSmall");
	textString:SetText(UI_SCALE);
	textString:ClearAllPoints();
	textString:SetPoint("RIGHT", self, "LEFT", -42, 0);

	_G[name.."High"]:Hide();

	self.ValueLabel = _G[name.."Low"];
	self.ValueLabel:ClearAllPoints();
	self.ValueLabel:SetPoint("LEFT", self, "RIGHT", 3, 2);

	BlizzardOptionsPanel_RegisterControl(self, self:GetParent());
end

Scripts.OnValueChanged:
if(userInput) then
	Graphics_EnableApply(self);
	self.newValue = value;
end
self.ValueLabel:SetText(format("%.2F", value));

Scripts.OnLoad:
if(InGlue()) then
	self:Hide();
else
	self.cvar = "useUiScale";
	self.SetValue = function (self, value)
		BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
	end
	VideoOptionsCheckbox_OnLoad(self);
end

Scripts.OnValueChanged:
CloseDropDownMenus()
if(self.onvaluechanged) then
	BlizzardOptionsPanel_Slider_OnValueChanged(self, value);
end
local quality = floor(value + 0.5);
self.displayValue:SetText(quality);
if ( quality == GetDefaultGraphicsQuality() ) then
	Graphics_Quality_Title:SetText(GRAPHICS_QUALITY_RECOMENDED);
else
	Graphics_Quality_Title:SetText(GRAPHICS_QUALITY);
end

CloseDropDownMenus()
if(self.onvaluechanged) then
	BlizzardOptionsPanel_Slider_OnValueChanged(self, value);
end
local quality = floor(value + 0.5);
self.displayValue:SetText(quality);
if ( quality == GetDefaultGraphicsQuality() ) then
	RaidGraphics_Quality_Title:SetText(GRAPHICS_QUALITY_RECOMENDED);
else
	RaidGraphics_Quality_Title:SetText(GRAPHICS_QUALITY);
end

Scripts.OnShow:
Display_EnableRaidSettingsText:SetPoint("LEFT", "RaidButton", "RIGHT", 30, 0);
if( not InGlue() ) then
	local inInstance, instanceType = IsInInstance();
	if ( inInstance and (instanceType == "raid" or instanceType == "pvp") ) then
		GraphicsOptions_SelectRaid();
		return;
	end
end
GraphicsOptions_SelectBase();

Scripts.OnLoad:
local name = self:GetName();
self.type = CONTROLTYPE_SLIDER;
self.cvar = "maxfps";
self.tooltip = OPTION_MAXFPS;
self.name = MAXFPS;

self.SetDisplayValue = self.SetValue;
self.SetValue = function (self, value)
	self:SetDisplayValue(value);
	BlizzardOptionsPanel_SetCVarSafe(self.cvar, value);
end
self.GetCurrentValue =
	function (self)
		return BlizzardOptionsPanel_GetCVarSafe(self.cvar);
	end;

_G[name.."Text"]:SetFontObject("OptionsFontSmall");
_G[name.."Text"]:SetText(MAXFPS);

_G[name.."High"]:Hide();

self.Label = _G[name.."Low"];
self.Label:ClearAllPoints();
self.Label:SetPoint("LEFT", self, "RIGHT", 3, 2);

VideoOptionsSlider_OnLoad(self);

Scripts.OnValueChanged:
if(userInput) then
	Graphics_EnableApply(self);
	self.newValue = value;
end
self.Label:SetFormattedText(FPS_FORMAT, value);

Scripts.OnClick:
local parent = (self:GetParent()):GetName();
local slider = _G[parent .. "MaxFPSSlider"];
if ( self:GetChecked() ) then
	VideoOptions_Enable(slider);
	slider.newValue = 8;
	slider:SetDisplayValue(8);
else
	VideoOptions_Disable(slider);
	slider.newValue = 0;
	slider:SetDisplayValue(0);
end
BlizzardOptionsPanel_CheckButton_OnClick(self);
Graphics_EnableApply(self);

